var miner_api = require('miner_api');

describe("A suite", function() {
	describe('Status API', function() {
		it('should list status of miner', function(done) {
			miner_api.getStatus('localhost', 4028, function(err, results) {
				expect(err).toEqual(null);
				expect(results).not.toEqual(null);
				expect(results.SUMMARY).toBeDefined();
				expect(results.DEVS).toBeDefined();
				expect(results.ACTIVE_POOL).toBeDefined();
				done();
			});
		});
	});
});
